
CREATE TABLE public.comunidades (
    codigo bigint NOT NULL,
    nombre character varying(40)
);


CREATE TABLE public.incidencias (
    id bigint NOT NULL,
    carretera character varying(20) NOT NULL,
    causa character varying(100),
    fechahora timestamp without time zone,
    cod_nivel integer NOT NULL,
    pk_inicial numeric(6,2) NOT NULL,
    pk_final numeric(6,2) NOT NULL,
    poblacion character varying(100),
    cod_provincia integer,
    cod_sentido integer,
    tipo_incidencia integer NOT NULL
);


CREATE TABLE public.niveles (
    nivel character varying(20),
    codigo integer NOT NULL
);


CREATE TABLE public.provincias (
    codigo integer NOT NULL,
    nombre character varying(40) NOT NULL,
    matricula character varying(8),
    cod_comunidad integer NOT NULL
);


CREATE TABLE public.sentidos (
    sentido character varying(40) NOT NULL,
    codigo integer NOT NULL
);


CREATE TABLE public.tiposincidencia (
    tipo character varying(30) NOT NULL,
    codigo integer NOT NULL
);















